import React, { Component } from 'react';
import './App.css';
import Headtitle from './Headtitle';
import Product from './Product'; 
// import dataProducts from 'http://localhost:4000/getData01'; //ko an toan vi co the nguoi khac co the truy cap dc 
import axios from 'axios';



const getProductData = () => {
  return axios.get('http://localhost:4000/getData01')
  .then ((res)=>(res.data))
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data:null
    } }


    componentWillMount() {
      if(this.state.data == null) {
        getProductData().then((res)=>{
          this.setState({
            data:res
          }) }) }}


    PrintData = () => {
      if(this.state.data != null) {
        return this.state.data.map((value,key)=>{
          return (<Product 
          key = {key}
          product_name = {value.product_name}
          product_prize = {value.product_prize}
          image = {value.image}
          />)
        })
      }
    }
  render() {
    // console.log(this.state.data);

  //   axios.get('http://localhost:4000/getData01') //truy cập đến đường dẫn
  //     .then(function (response) {
  //       // handle success
  //       console.log(response.data);
  //     })
  //     .catch(function (error) {
  //       // handle error
  //       console.log(error);
  //     })
  //     .finally(function () {
  //       // always executedy
  //     });
    return (

      <div>

        <Headtitle />

        <div className="container">
          <div className="row">
            {this.PrintData()}


          </div>
        </div>
      </div>
    );
  }
}

export default App;
